(()=>{function o(n,c){let a=[...document.querySelectorAll(`[data-render=${n}]`)];for(let r of a){let d=r.dataset;fetch(c,{method:"get"}).then(e=>e.text()).then(e=>{let s=Array.from(e.matchAll(/{{(.*?)}}/g)).map(t=>t[1]);for(let t of s)e=e.replace(`{{${t}}}`,d[t]||"");r.innerHTML=e})}}window.renderHtml=o;})();
